import { ProdutosService } from './../../../services/produtos.service';
import { IProduto } from './../../../model/IProduto.model';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-listar-produtos',
  templateUrl: './listar-produtos.component.html',
  styleUrls: ['./listar-produtos.component.css'],
})
export class ListarProdutosComponent implements OnInit {
  listaProdutos: IProduto[] = [];

  constructor(private produtosService: ProdutosService) {}

  ngOnInit(): void {
    this.carregarProdutos();
  }

  carregarProdutos(): void {
    this.produtosService.buscarTodos().subscribe(retorno =>{
      this.listaProdutos = retorno;
    })
  }

  deletar(produto: IProduto ): void{
    this.produtosService.excluir(produto.id!).subscribe(()=>{
     this.produtosService.exibirMensagem('SISTEMA',
    `${produto.nome} foi excluido com sucesso!`,
    'toast error '
    );
    })
    
<h1 class="my-3 text-primary">
  Listar Produtos
  <button
    type="buttom"
    routerLink="/produtos/cadastrar"
    class="btn btn-success mt-2 float-right"
  >
    Cadastrar
  </button>
</h1>
<div class="card shadow">
  <table class="table table-responsive table striped">
    <thead>
      <tr>
        <th>#</th>
        <th>Nome</th>
        <th>Validade</th>
        <th>Valor</th>
        <th>Ações</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let prod of listaProdutos">
        <td>{{ prod.id }}</td>
        <td>{{ prod.nome }}</td>
        <td>{{ prod.validade | date: "shortDate" }}</td>
        <td>{{ prod.precoProduto | currency: "BRL" }}</td>
        <td>
          <button class="btn btn-warning"
          routerLink = "/produtos/atualizar/{{prod.id}}"
          >
            <i class="fa fa-pencil" aria-hidden="true"></i>
          </button>
          <button class="btn btn-danger ml-2" [swal]="{
            title: 'ATENÇÃO!!!',
            text: 'Deseja realmente excluir o produto:' + prod.nome + '?',
            showDenyButton: true,
            denyButtonText: 'Não',
            confirmButtonText: 'Sim, excluir',
            icon: 'warning'
            }"
            (confirm)="deletar(prod)"
            >
            <i class="fa fa-trash-o" aria-hidden="true"></i>
          </button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
